import React from 'react'
import Child from "./child"
import { useState } from 'react';

export default function Parent() {
    const [data, setData] = useState('Data from Parent');
    const handleChange = () => {setData("New data from Parent")};
    return (
        <>
            <h2>Function Component - Parent</h2>    
            {/* Can not use this. for function component */}
            <button onClick = {() => handleChange} >Click to change the data ( From Parent to child)</button>       
            <Child parentToChild={data}/>            
        </>
    )
}
