// src/js/reducers/index.js

const initialState = {
    articles: []
  };

  function rootReducer(state = initialState, action) {
    if (action.type === "ADD_ARTICLE") {
        //state.articles.push(action.payload); // This command pushes the action payload into the initial state
        articles: state.articles.concat(action.payload) //The resulting state is just a copy of the initial state
        //use concat, slice, or the spread operator for arrays
        //use Object.assign or object spread of objects

    }
    return state;
  };

  export default rootReducer;
