import React from 'react'
import Child from "./child"
import { useState } from 'react';

export default function Parent() {    
    const [isToggle, setToggle] = useState(true);
    const [data, setData] = useState('Data from Parent');
    const handleChange = () => {        
        setData( (isToggle==true)?"New data from Parent":"Old Data from Parent");
        setToggle(!isToggle);
    };
    return (
        <>
            <h2>Function Component - Parent</h2>    
            {/* Can not use this. for function component, neeed parentthese () for the onClick function */}
            <button onClick = {() => handleChange()} >Click to change the data ( From Parent to child)</button>       
            <Child parentToChild={data}/>            
        </>
    )
}
