    const [isToggle, setToggle] = useState(true);
    const [data, setData] = useState('Data from Parent');
    {/* function to toggle the data that we want to pass to the Child component  */}
    const handleChange = () => {        
        setData( (isToggle==true)?"New data from Parent":"Old Data from Parent");
        setToggle(!isToggle);
    };

    const receiveChildToParent = () => {

    };

    return (
        <>
            <h2>Function Component - Parent - Pass data to Child</h2>    
            {/* Can not use this. for function component, neeed parentthese () for the onClick function 
                pass the data to the Child component, and parentToChild is the name of the prop
            */}
